update.foreach:
update t_goods_message
set
    sales_volume    =   sales_volume + #{item.goodsCnt}
where
    goods_code    =   #{item.goodsCode}

mapper.update:
update t_goods_message
set
    sales_volume   =   sales_volume + #{goodsCnt},
    goods_stock   =   goods_stock - #{goodsCnt}
where  goods_code  =    #{goodsCode};

update t_goods_message

    goods_stock    =   goods_stock + #{goodsCnt},
    sales_volume   =   sales_volume - #{goodsCnt}

where  goods_code  =    #{goodsCode}

update t_goods_message
set
    goods_star = #{param2}
where
    is_delete = 0
    and goods_code    =   #{param1}
