mapper.update:
update t_goods_message
set
    sales_volume    =   sales_volume + #{param2}
where
    goods_code    =   #{param1}

update t_goods_message
set
    goods_stock    =   goods_stock + #{param2}
where
    goods_code    =   #{param1}

update t_goods_message
set
    goods_star = #{param2}
where
    is_delete = 0
    and goods_code    =   #{param1}
